import React, {Fragment} from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    backgroundColor: theme.palette.background.paper,
  },
}));

const useStylesHitItem = makeStyles({
  root: {
    minWidth: 275,
  },
  bullet: {
    display: 'inline-block',
    margin: '0 2px',
    transform: 'scale(0.8)',
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
});
/*
		<Fragment key={i}> 
					<ListItem button> 
						<ListItemText primary={model.text} />
						<ListItemText primary={model.prob} />
					</ListItem>
					<Divider />

				</Fragment>

*/
function HitItem(i, model){
	const classes = useStylesHitItem();
	return (
		<Card className={classes.root}>
		  <CardContent>
			<Typography className={classes.title} color="textSecondary" gutterBottom>
			  {model.prob}
			</Typography>
			<Typography variant="h5" component="h2">
			  {model.text}
			</Typography>
			<Typography className={classes.pos} color="textSecondary">
			  adjective
			</Typography>
			<Typography variant="body2" component="p">
			  well meaning and kindly.
			  <br />
			  {'"a benevolent smile"'}
			</Typography>
		  </CardContent>
		  <CardActions>
			<Button size="small">Learn More</Button>
		  </CardActions>
		</Card>
	)
}
export default function ListDividers(props) {
	const classes = useStyles();

	const tags = props.tags;
	//const probs = props.probs;
	const listItems = tags.map((model, i) => HitItem(i, model));
	/*const listItems = tags.map((each_tag, i) => {
		return 	<Fragment key={i}> 
					<ListItem button> 
						<ListItemText primary={each_tag} />
					</ListItem>
					<Divider />

				</Fragment>
	});*/
	return (
		<List component="nav" className={classes.root} aria-label="mailbox folders">
		  {listItems}
		</List>
  );
}

